import {createSlice} from '@reduxjs/toolkit';

const initialState = {
    items : [] ,
    totalQuantity : 0 ,
};

const cartSlice = createSlice({
    name: 'cart',
    initialState ,
    reducers : {
        replaceCart(state, action) {
            state.totalQuantity = action.payload.totalQuantity;
            state.items = action.payload.items;
        },
        addItemtoCart (state , action){
            const newItem = action.payload;
            const existingItem = state.items.find(item => item.id === newItem.id);
            state.totalQuantity++;
            if(!existingItem){
                state.items.push({
                    id : newItem.id , 
                    price : newItem.price , 
                    quantity : 1 , 
                    totalPrice : newItem.price ,
                    name :  newItem.title
                });
            }else{
                existingItem.quantity ++ ;
                existingItem.totalPrice += newItem.price;
            }
        },
        removeItemtoCart (state , action){
            const id = action.payload ;
            const existingItem = state.items.find(item => item.id === id);
            state.totalQuantity--;
            if(existingItem.quantity === 1){
                state.items = state.items.filter(item => item.id !== id);
            }else{
                existingItem.quantity--;
                existingItem.totalPrice -= existingItem.price
            }
        },
    },
})

// here we re going to create our own action creators let's see how we can do that
// for general reducer function redux automatically creates a method for us which contains { type : '' , payload : ''}
// but in our own action creators we have to create our own methods 
const sendCartData = (cartData) =>{
    return
}

export const ModifyCart = cartSlice.actions ;

export default cartSlice.reducer ;
