import {configureStore } from '@reduxjs/toolkit';
import showCartSlice from './uiSlice';
import cartSlice, { ModifyCart } from './CartSlice.js'

const store = configureStore({
    reducer : {
        showCart : showCartSlice ,
        ModifyCart
    }
})

export default store ;
