import {createSlice} from '@reduxjs/toolkit';

const initialState = {
    isShow : false ,

}

const showCartSlice = createSlice({
    name : 'ShowCart',
    initialState,
    reducers : {
        toggle (state) {
            state.isShow = !state.isShow ;
        },
    }
})

export const  cartAction = showCartSlice.actions ;

export default showCartSlice.reducer ;
