import { useEffect } from 'react';
import { useSelector , useDispatch } from 'react-redux';
import Cart from './components/Cart/Cart';
import Layout from './components/Layout/Layout';
import Products from './components/Shop/Products';
import { cartAction } from './components/store/uiSlice.js';

function App() {

  const isShow = useSelector(state => state.showCart.isShow);
  const cart = useSelector(state => state.cart);
  const dispatch = useDispatch();
  const nitification = useSelector(state => state.showCart.nitification);

  useEffect(()=>{
    const sendCartData = async ()=>{
      dispatch(cartAction.showNotification({
        status : 'pending',
        title : 'sending...',
        message : 'Sending Cart Data! ..'
      }))
      // cart.json is a firebase specific and it will create a new cart node in the database 
      const response = await fetch('https://advance-redux-6e3fd-default-rtdb.firebaseio.com/cart.json' ,{
        method : 'PUT',
        body : JSON.stringify(cart),
      })

      if(!response.ok){
        throw new Error('Sending cart data is failed...');
      };

      dispatch(cartAction.showNotification({
        status : 'success',
        title : 'success!...',
        message : 'Send Cart Data successfully! ..'
      }))
    }
    sendCartData().catch(error => {
        dispatch(cartAction.showNotification({
        status : 'error',
        title : 'Error!...',
        message : 'Sending Cart Data Failed! ..'
      })) 
    });

  } ,[cart ,dispatch])

  return (
    <>
    <Layout>
      {isShow && <Cart /> }
      <Products />
    </Layout>
    </>
  );
}

export default App;
